mapper.select:
		SELECT B.* FROM(
         SELECT ROW_NUMBER() OVER(ORDER BY square_num) AS rn, 
         A.* FROM(
            SELECT * FROM board_square
            
               WHERE nick LIKE CONCAT('%',#{search},'%')
               WHERE square_title LIKE CONCAT('%',#{search},'%')
            
            ORDER BY square_num DESC
         )A
      )B WHERE rn BETWEEN #{b} and #{e}
