mapper.select:
SELECT a.*, b.party_count AS party_now_member FROM (SELECT * FROM party WHERE id=#{id} ORDER BY party_num DESC) a LEFT JOIN (SELECT party_num, count(*) AS party_count FROM party_member GROUP BY party_num) b ON a.party_num = b.party_num WHERE
a.party_num LIKE CONCAT('%',${keyword},'%') a.party_id LIKE CONCAT('%',${keyword},'%')
AND a.party_service=#{keynum} AND a.party_subservice=#{keynum}
;

SELECT b.nick, b.mycharge, b.mystartday, a.party_num, a.party_service, a.party_subservice, a.party_title, a.party_charge, a.party_available, a.party_end FROM (SELECT * FROM party WHERE id=#{id}) a LEFT JOIN party_member b ON a.party_num = b.party_num WHERE b.id IS NOT NULL
AND a.party_service=#{keynum} AND a.party_subservice=#{keynum}
AND a.party_num LIKE CONCAT('%',${keyword},'%')
AND b.mystartday BETWEEN #{start} AND #{end}
ORDER BY b.mystartday DESC
